const App = () => {
    const [showTimeline, setShowTimeline] = useState(false);
  
    const handleButtonClick = () => {
      setShowTimeline(true);
    };
  
    const fadingMessages = [
      'Happy Birthday, my love!',
      'You mean the world to me.',
      'Here\'s a special gift for you.',
    ];
  
    const timelineData = [
      { date: '2020', event: 'Our First Date' },
      { date: '2021', event: 'Moved in Together' },
      // Add more milestones as needed
    ];
  
    return (
      <div>
        {!showTimeline ? (
          <FadingMessages messages={fadingMessages} onButtonClick={handleButtonClick} />
        ) : (
          <Timeline milestones={timelineData} />
        )}
      </div>
    );
  };
  
  export default App;
