// components/TimelineItem.js
import React from 'react';
import styled from 'styled-components';

const TimelineItem = ({ photo }) => {
  return (
    <ItemContainer>
      <MonthTag>{photo.month}</MonthTag>
      <ImageContainer>
        <img src={photo.url} alt={`Photo taken on ${photo.date}`} />
        <Overlay>
          <DateTag>{photo.date}</DateTag>
          <Description>{photo.description}</Description>
        </Overlay>
      </ImageContainer>
    </ItemContainer>
  );
};

const ItemContainer = styled.div`
  position: relative;
  margin-right: 20px; /* Adjust spacing between timeline items */
`;

const MonthTag = styled.div`
  font-size: 16px;
  font-weight: bold;
  margin-bottom: 10px;
`;

const ImageContainer = styled.div`
  position: relative;
  cursor: pointer;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.1);
  }
`;

const Overlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  padding: 20px;
  background: rgba(0, 0, 0, 0.7);
  opacity: 0;
  transition: opacity 0.3s ease;
  cursor: default;

  &:hover {
    opacity: 1;
  }
`;

const DateTag = styled.div`
  font-size: 14px;
  font-weight: bold;
`;

const Description = styled.p`
  font-size: 12px;
  color: #fff;
`;

export default TimelineItem;
