// components/Timeline.js
import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';
import TimelineItem from './components/TimelineItem';

const bottomUpSweep = keyframes`
  from {
    transform: translateY(100%);
  }
  to {
    transform: translateY(0);
  }
`;

const Timeline = ({ photos, backgroundColor }) => {
  const [hoveredMonth, setHoveredMonth] = useState(null);

  const handleHover = (month) => {
    setHoveredMonth(month);
  };

  return (
    <TimelineContainer backgroundColor={backgroundColor}>
      <MonthTag>{hoveredMonth}</MonthTag>
      <TimelineWrapper>
        {photos.map((photo, index) => (
          <TimelineItem key={index} photo={photo} onHover={handleHover} />
        ))}
      </TimelineWrapper>
    </TimelineContainer>
  );
};

const TimelineContainer = styled.div`
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  overflow: hidden;
  background-color: ${({ backgroundColor }) => backgroundColor || 'black'};
  height: 100vh;
`;

const MonthTag = styled.div`
  position: absolute;
  top: 20px;
  left: 50%;
  transform: translateX(-50%);
  font-size: 20px;
  font-weight: bold;
  color: white;
  z-index: 1;
`;

const TimelineWrapper = styled.div`
display: flex;
overflow-x: auto;
padding-bottom: 20px;
animation: ${bottomUpSweep} 1s ease;

/* Set the width to show only 5 images at a time */
width: calc(100% - 40px); /* Adjust the width as needed */
margin: 0 20px;
box-sizing: content-box;
`;

export default Timeline;
