import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';
import TimelineItem from './components/TimelineItem';

const bottomUpSweep = keyframes`
  from {
    transform: translateY(100%);
  }
  to {
    transform: translateY(0);
  }
`;

const Timeline = ({ photos, backgroundColor }) => {
  const [hoveredMonth, setHoveredMonth] = useState(null);
  const [selectedPhoto, setSelectedPhoto] = useState(null);

  const handleHover = (month) => {
    setHoveredMonth(month);
  };

  const handlePhotoClick = (photo) => {
    setSelectedPhoto(photo);
  };

  const handleClearSelection = () => {
    setSelectedPhoto(null);
  };

  return (
    <TimelineContainer backgroundColor={backgroundColor}>
      <MonthTag>{hoveredMonth}</MonthTag>
      <CenterWrapper>
        <TimelineWrapper>
          {photos.map((photo, index) => (
            <TimelineItem
              key={index}
              photo={photo}
              onHover={handleHover}
              onClick={handlePhotoClick}
            />
          ))}
        </TimelineWrapper>
      </CenterWrapper>
      {selectedPhoto && (
        <ModalOverlay onClick={handleClearSelection}>
          <ModalContent>
            <ModalImageContainer>
              <img
                src={process.env.PUBLIC_URL + selectedPhoto.url}
                alt={`Selected Photo taken on ${selectedPhoto.date}`}
              />
            </ModalImageContainer>
            <ModalInfoContainer>
              <DateTag>{selectedPhoto.date}</DateTag>
              <Description>{selectedPhoto.description}</Description>
            </ModalInfoContainer>
          </ModalContent>
        </ModalOverlay>
      )}
    </TimelineContainer>
  );
};
const TimelineContainer = styled.div`
  position: relative;
  font-family: 'Arial', sans-serif;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  overflow: hidden;
  background: linear-gradient(to top, rgba(63, 64, 84, 1) 79%, rgba(135, 31, 173, 0.8) 100%);
  height: 100vh;
`;

const MonthTag = styled.div`
  position: absolute;
  top: 20px;
  left: 50%;
  transform: translateX(-50%);
  font-size: 20px;
  font-weight: bold;
  color: grey;
  z-index: 1;
`;

const CenterWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100%;
`;

const TimelineWrapper = styled.div`
  display: flex;
  overflow-x: auto;
  padding-bottom: 20px;
  animation: ${bottomUpSweep} 1s ease;

  /* Set the width to show only 5 images at a time */
  width:  1500px; /* Adjust the width as needed */
  height: 50%;
  margin: 0 20px;
  box-sizing: content-box;

  /* Adjust the scroll bar size */
  scrollbar-width: thin; /* "thin" for a thinner scrollbar */
  scrollbar-color: white black; /* Adjust colors as needed */
`;

export default Timeline;
