// components/FadingMessages.js
import React from 'react';
import styled, { keyframes, css } from 'styled-components';

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;


const BlackScreen = styled.div`
  background-color: #3f4054;
  color: white;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
`;

const ContinueBtn = styled.button`
  background-color: pink;
`;

const Welcome = ({ messages, onButtonClick }) => {
  return (
    <BlackScreen>
      {messages.map((message, index) => (
        <FadeInDiv key={index} index={index + 1}>
        {message}
        </FadeInDiv>
      ))}
      <fadeInDiv>
      <ContinueBtn onClick={onButtonClick}>Continue</ContinueBtn>
      </fadeInDiv>
      
    </BlackScreen>
  );
};

const FadeInDiv = styled.div`
  ${({ index }) => css`
    opacity: 0;
    animation: ${fadeIn} 1s ease ${index}s forwards;
  `}
`;

export default Welcome;
