import Welcome from "./Welcome";
import Timeline from "./Timeline";
import React from "react";
import { useState } from "react";

const App = () => {
    const [showTimeline, setShowTimeline] = useState(false);
  
    const handleButtonClick = () => {
      setShowTimeline(true);
    };
  
    const fadingMessages = [
      'Hi Pookie',
      'Happy 21st Birthday',
      'I have been planning this for a little while.', 
      'I hope you like it, I Love you'
    ];
  
    const timelineData = [
      { date: 'June 11th', event: 'Lego Night' },
      { date: '', event: 'Moved in Together' },
      { date: '', event: ''},
      { date: '', event: ''},
      { date: '', event: ''},
      { date: '', event: ''},
      { date: '', event: ''},
      { date: '', event: ''},
      // Add more milestones as needed
    ];
  
    return (
      <div>
        {!showTimeline ? (
          <Welcome messages={fadingMessages} onButtonClick={handleButtonClick} />
        ) : (
          <Timeline milestones={timelineData} />
        )}
      </div>
    );
  };
  
  export default App;
