import Welcome from "./Welcome";
import Timeline from "./Timeline";
import React, { useState } from "react";

const App = () => {
  const [showTimeline, setShowTimeline] = useState(false);

  const handleButtonClick = () => {
    setShowTimeline(true);
  };

  const fadingMessages = [
    'Hi Pookie',
    'Happy 21st Birthday',
    'I have been planning this for a little while.', 
    'I hope you like it, I Love you'
  ];

  // Placeholder photos array
  const photos = [
    { url: './images/June10th.PNG', month: 'June', date: '2023-06-10', description: 'I love you' },
    { url: 'path/to/photo2.jpg', month: 'February', date: '2023-02-15', description: 'Description 2' },
    // Add more photo objects with their respective details
  ];

  return (
    <div>
      {!showTimeline ? (
        <Welcome messages={fadingMessages} onButtonClick={handleButtonClick} />
      ) : (
        <Timeline photos={photos} />
      )}
    </div>
  );
};

export default App;
