import React, { useState } from 'react';
import styled from 'styled-components';

const TimelineItem = ({ photo, onHover, onClick }) => {
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
    onHover(photo.month);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  const handleItemClick = () => {
    onClick(photo);
  };

  return (
    <ItemContainer>
      <ImageContainer
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        onClick={handleItemClick}
        isHovered={isHovered}
      >
        <img src={process.env.PUBLIC_URL + photo.url} alt={`Photo taken on ${photo.date}`} />
      </ImageContainer>
    </ItemContainer>
  );
};


const ItemContainer = styled.div`
  position: relative;
  margin-right: 20px;
`;

const ImageContainer = styled.div`
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  transition: transform 0.3s ease;
  overflow: hidden;
  width: 00px; /* Set the desired width */
  height: 400px; /* Set the desired height */

  &:hover {
    transform: scale(1.1);
  }

  img {
    max-width: 100%;
    max-height: 100%;
    object-fit: cover; /* Maintain aspect ratio and cover the container */
    display: block;
  }
`;

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5); /* Semi-transparent black background */
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalContent = styled.div`
  display: flex;
  background: white; /* White background for the modal content */
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const ModalImageContainer = styled.div`
  flex: 1;
  overflow: hidden;
  margin-right: 20px;

  img {
    max-width: 100%; /* Ensures the image doesn't exceed the container width */
    max-height: 700px; /* Set the maximum height as needed */
    object-fit: contain; /* Maintains aspect ratio and fits the image within the container */
    display: block;
  }
`;

const ModalInfoContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
`;

const DateTag = styled.div`
  font-size: 18px;
  font-weight: bold;
  margin-bottom: 10px;
`;

const Description = styled.div`
  color: pink;
`;

export default TimelineItem;
